#include "Klondike.h"
#include "TableLayout.h"
#include "UInterface.h"

namespace klondike {

Klondike::~Klondike() {
	// TODO Auto-generated destructor stub
}

void Klondike::startGame()
{
	TableLayout table;
	UInterface display;

	table.Deal();

	bool game_is_finished = false;

	UInterface::printMessage("Game started");

	do
	{
		display.updateTable(table);
		display.printTableLayout();


		game_is_finished = true;
	}while(!game_is_finished);
	//table->deal();


}

} /* namespace klondike */

